const XHR = require("engine.io/lib/transports/polling");
const JSONP = require("engine.io/lib/transports/polling-jsonp");

/**
 * Export transports.
 */

module.exports = exports = {
  polling: polling,
  websocket: require("engine.io/lib/transports/websocket")
};

/**
 * Export upgrades map.
 */

exports.polling.upgradesTo = ["websocket"];

/**
 * Polling polymorphic constructor.
 *
 * @api private
 */

function polling(req) {
  if ("string" === typeof req._query.j) {
    return new JSONP(req);
  } else {
    return new XHR(req);
  }
}
